INSERT INTO `wp_postmeta` VALUES ('967', '174', 'customer_need_text', 'CASHHOPPERS is a photo scavenger hunt contest you play on your cell phone for CASH and other prizes There a Daily HOPs where you can complete against other Hoppers from around the globe. Or you can join private HOPs played at trade shows, fairs, weddings receptions, school fundraisers, and more!');
