INSERT INTO `wp_postmeta` VALUES ('971', '174', 'solution_text', '<ul>\r\n	<li>Download the <a href=\"http://cashhoppers.com/#\">FREE app.</a></li>\r\n	<li>Find the HOP you want to play(enter the password if it\'s a private HOP).</li>\r\n	<li>Start taking pictures on the list (Remember, you have to be visible in every picture to be eligible.)</li>\r\n	<li>Add an optional comment and share your picture on Facebook.</li>\r\n	<li>Wait to see if you\'ve won!</li>\r\n</ul>\r\nIt\'s that easy !');
