INSERT INTO `wp_postmeta` VALUES ('154', '33', 'why_do_you_need', '<h1>Why do you need a mobile application?</h1>\r\n<ul class=\"yellow\">\r\n	<li>The market for mobile devices is experiencing tremendous growth. The number of smartphones, tablets and other mobile device being used is increasing each day.</li>\r\n	<li>The ability to attract the customers all over the world.</li>\r\n	<li>A large market of potential customers for your products and services.</li>\r\n	<li>A mobile application will help to simplify the process of getting products and services to your customers.</li>\r\n	<li>many of your competitors have already done this or are considering to create a mobile application for their business.</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES ('155', '33', '_why_do_you_need', 'field_525ff52a91f21');
