INSERT INTO `wp_posts` VALUES ('33', '1', '2013-10-17 10:53:01', '2013-10-17 10:53:01', 'Swan Software Solutions is a fast-growing mobile application development company in Indianapolis. Our company develops apps for iPhone, Android and BlackBerry platforms.\r\n\r\nSwan Software Solutions will create a perfect application to meet all your needs and will help you make a good impression on your potential clients. We have extensive in mobile application development, great knowledge of all technical aspects and a highly-professional team.', 'Mobile Application Development', '', 'publish', 'open', 'open', '', 'mobile-application-development', '', '', '2013-10-18 09:40:10', '2013-10-18 09:40:10', '', '0', 'http://localhost/swanBootstrap/?page_id=33', '0', 'page', '', '0');
